import React from 'react'
import { useState, useEffect } from 'react'
import { Link, useParams, useNavigate } from 'react-router-dom'
import Button from '@mui/material/Button'
import IconButton from '@mui/material/IconButton'
import DeleteIcon from '@mui/icons-material/Delete'

export const NotePage = () => {

  const params = useParams()
  const noteId = params.id
  const navigate = useNavigate()

  let [note, setNote] = useState(null)

  const getNote = async () => {
    if (noteId === 'new')  return
    let response = await fetch(`/api/notes/${noteId}`)
    let data = await response.json()
    setNote(data)
  }

  useEffect(() => {
    getNote()
  }, [noteId])

  const saveHandle = async () => {
    if (noteId === 'new' && note != null) {
      fetch(`/api/notes/create`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(note)
      })
    } else {
      fetch(`/api/notes/${noteId}/update`, {
        method: "PUT",
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(note)
      })
    }
    navigate('/notes')
  }

  const deleteHandle = async () => {
    fetch(`/api/notes/${noteId}/delete`, {
      method: 'DELETE',
      headers: {
        'Content-Type': 'application/json'
      }
    })
    navigate('/notes')
  }

  return (
    <div className='note'>
      <textarea defaultValue={note?.body} spellCheck="false" onChange={(e) => setNote({...note, 'body': e.target.value})}></textarea>
      <div className='note-btn'>
        <Button onClick={saveHandle}>Save</Button>
        <Button><Link to='/notes'>Cancel</Link></Button>
        {noteId !== 'new' &&
          <Button className='deleteBtn' onClick={deleteHandle} color='error'>Delete</Button>
        }
      </div>
    </div>
  )
}

export default NotePage
