import React from 'react'
import { useState, useEffect } from 'react'
import { Link, useParams } from 'react-router-dom'
import Button from '@mui/material/Button'
import IconButton from '@mui/material/IconButton'
import DeleteIcon from '@mui/icons-material/Delete'

export const NotePage = ({history}) => {

  const params = useParams()
  const noteId = params.id

  let [note, setNote] = useState(null)

  const getNote = async () => {
    let response = await fetch(`/api/notes/${noteId}`)
    let data = await response.json()
    setNote(data)
  }

  useEffect(() => {
    getNote()
  }, [noteId])

  const saveHandle = async () => {
    fetch(`/api/notes/${noteId}/update`, {
      method: "PUT",
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(note)
    })
    history.push('/notes')
  }

  return (
    <div className='note'>
      <textarea defaultValue={note?.body} onChange={(e) => setNote({...note, 'body': e.target.value})}></textarea>
      <div className='note-btn'>
        <Button onClick={saveHandle}>Save</Button>
        <Button><Link to='/notes'>Cancel</Link></Button>
        <IconButton aria-label="delete" size="medium" color='warning'>
          <DeleteIcon />
        </IconButton>
      </div>
    </div>
  )
}

export default NotePage
